const SB = {
  h: (extra={}) => ({ "Content-Type":"application/json", apikey:CONFIG.SUPABASE_KEY, Authorization:"Bearer "+CONFIG.SUPABASE_KEY, ...extra }),
  url: (t,q="") => CONFIG.SUPABASE_URL+"/rest/v1/"+t+q,
  rpc: (fn) => CONFIG.SUPABASE_URL+"/rest/v1/rpc/"+fn,
  async get(t,q="") {
    try {
      const r = await fetch(SB.url(t,q), {headers:SB.h()});
      const d = await r.json();
      if (!r.ok) { console.warn("SB.get error",t,d); return []; }
      return Array.isArray(d) ? d : [];
    } catch(e) { console.error("SB.get",e); return []; }
  },
  async post(t,body) {
    try {
      const r = await fetch(SB.url(t), {method:"POST", headers:SB.h({"Prefer":"return=representation"}), body:JSON.stringify(body)});
      const d = await r.json();
      if (!r.ok) { console.warn("SB.post error",t,d); return null; }
      return Array.isArray(d) ? d[0] : d;
    } catch(e) { console.error("SB.post",e); return null; }
  },
  async patch(t,q,body) {
    try {
      const r = await fetch(SB.url(t,q), {method:"PATCH", headers:SB.h({"Prefer":"return=representation"}), body:JSON.stringify(body)});
      const d = await r.json();
      if (!r.ok) { console.warn("SB.patch error",t,d); return null; }
      return Array.isArray(d) ? d[0] : d;
    } catch(e) { console.error("SB.patch",e); return null; }
  },
  async del(t,q) {
    try {
      const r = await fetch(SB.url(t,q), {method:"DELETE", headers:SB.h()});
      return r.ok;
    } catch(e) { return false; }
  },
  async call(fn,body={}) {
    try {
      const r = await fetch(SB.rpc(fn), {method:"POST", headers:SB.h(), body:JSON.stringify(body)});
      const d = await r.json();
      return d;
    } catch(e) { return null; }
  },
};


const fmtEta = (mins) => {
  const h = Math.floor(mins/60);
  const m = mins % 60;
  return (h > 0 ? h+"h " : "") + (m > 0 ? m+"min" : "");
};

