//  LOGIN 
function LoginPage({go, setUser, showToast}) {
  const [mode, setMode]   = React.useState("login");
  const [form, setForm]   = React.useState({name:"",phone:"",password:"",confirm:""});
  const [loading, setLoading] = React.useState(false);

  async function handleLogin() {
    if (!form.phone||!form.password) { showToast("Phone and password required","error"); return; }
    setLoading(true);
    const phone = form.phone.replace(/^0/,"256");
    const rows = await SB.get("users","?phone=eq."+phone+"&role=eq.customer&is_active=eq.true");
    if (!Array.isArray(rows)||rows.length===0) { showToast("Account not found. Please register.","error"); setLoading(false); return; }
    const u = rows[0];
    if (u.password_hash===form.password||u.password_hash==="PLAIN:"+form.password) {
      const ud = {id:u.id,full_name:u.full_name,phone:u.phone};
      setUser(ud); localStorage.setItem("raylane_user",JSON.stringify(ud));
      showToast("Welcome back, "+u.full_name.split(" ")[0]+"!"); go("home");
    } else showToast("Incorrect password","error");
    setLoading(false);
  }

  async function handleRegister() {
    if (!form.name||!form.phone||!form.password) { showToast("All fields required","error"); return; }
    if (form.password!==form.confirm) { showToast("Passwords do not match","error"); return; }
    if (form.password.length<6) { showToast("Password must be at least 6 characters","error"); return; }
    setLoading(true);
    const phone = form.phone.replace(/^0/,"256");
    const existing = await SB.get("users","?phone=eq."+phone);
    if (Array.isArray(existing)&&existing.length>0) { showToast("Account already exists with this phone","error"); setLoading(false); return; }
    const result = await SB.post("users",{full_name:form.name.trim(),phone,password_hash:form.password,role:"customer",is_active:true});
    const u = Array.isArray(result)?result[0]:result;
    if (u && u.id) {
      const ud = {id:u.id,full_name:u.full_name,phone:u.phone};
      setUser(ud); localStorage.setItem("raylane_user",JSON.stringify(ud));
      showToast("Account created! Welcome, "+form.name.split(" ")[0]+"!"); go("home");
    } else showToast("Registration failed. Please try again.","error");
    setLoading(false);
  }

  return (
    <div style={{maxWidth:420,margin:"0 auto",padding:"60px 20px"}} className="fade">
      <button className="btn btn-ghost" onClick={()=>go("home")}> Home</button>
      <div style={{textAlign:"center",marginBottom:28,marginTop:16}}>
        <img src={LOGO_IMG} alt="Raylane Express" style={{height:56,width:"auto",marginBottom:12}} />
        <h2 style={{fontFamily:"'Fraunces',serif",fontSize:26,fontWeight:900,color:"var(--navy)"}}>{mode==="login"?"Welcome Back":"Create Account"}</h2>
        <p style={{color:"var(--muted)",marginTop:4,fontSize:14}}>{mode==="login"?"Sign in to access your bookings and auto-fill details":"Register for faster booking & booking history"}</p>
      </div>
      <div style={{display:"flex",background:"#f1f5f9",borderRadius:10,padding:4,marginBottom:24}}>
        {["login","register"].map(m=>(
          <button key={m} onClick={()=>setMode(m)} style={{flex:1,padding:"10px",border:"none",borderRadius:8,cursor:"pointer",fontFamily:"inherit",fontWeight:700,fontSize:14,background:mode===m?"#fff":"transparent",color:mode===m?"var(--navy)":"var(--muted)",boxShadow:mode===m?"0 1px 4px rgba(0,0,0,.1)":"none",transition:"all .15s"}}>
            {m==="login"?"Sign In":"Register"}
          </button>
        ))}
      </div>
      <div className="card">
        <div style={{display:"flex",flexDirection:"column",gap:16}}>
          {mode==="register"&&<div><label style={{fontSize:12,fontWeight:700,color:"var(--muted)",textTransform:"uppercase",letterSpacing:".5px",display:"block",marginBottom:8}}>Full Name *</label><input className="input" placeholder="Your full name" value={form.name} onChange={e=>setForm(f=>({...f,name:e.target.value}))} /></div>}
          <div><label style={{fontSize:12,fontWeight:700,color:"var(--muted)",textTransform:"uppercase",letterSpacing:".5px",display:"block",marginBottom:8}}>Phone Number *</label><input className="input" placeholder="07XX XXX XXX" type="tel" value={form.phone} onChange={e=>setForm(f=>({...f,phone:e.target.value}))} /></div>
          <div><label style={{fontSize:12,fontWeight:700,color:"var(--muted)",textTransform:"uppercase",letterSpacing:".5px",display:"block",marginBottom:8}}>Password *</label><input className="input" placeholder="Enter password" type="password" value={form.password} onChange={e=>setForm(f=>({...f,password:e.target.value}))} onKeyDown={e=>e.key==="Enter"&&(mode==="login"?handleLogin():handleRegister())} /></div>
          {mode==="register"&&<div><label style={{fontSize:12,fontWeight:700,color:"var(--muted)",textTransform:"uppercase",letterSpacing:".5px",display:"block",marginBottom:8}}>Confirm Password *</label><input className="input" placeholder="Repeat password" type="password" value={form.confirm} onChange={e=>setForm(f=>({...f,confirm:e.target.value}))} onKeyDown={e=>e.key==="Enter"&&handleRegister()} /></div>}
          <button className="btn btn-primary" style={{width:"100%",padding:14,fontSize:15,marginTop:4}} onClick={mode==="login"?handleLogin:handleRegister} disabled={loading}>
            {loading?"Please wait...":(mode==="login"?"Sign In ":"Create Account ")}
          </button>
        </div>
      </div>
      <p style={{textAlign:"center",fontSize:13,color:"var(--muted)",marginTop:14}}>
        {mode==="login"?"No account? ":"Already registered? "}
        <span onClick={()=>setMode(mode==="login"?"register":"login")} style={{color:"var(--blue)",cursor:"pointer",fontWeight:600}}>{mode==="login"?"Register here":"Sign in"}</span>
      </p>
    </div>
  );
}

